CREATE TABLE IF NOT EXISTS producto ( num_serie INT NOT NULL AUTO_INCREMENT, nombre_producto VARCHAR(50) NOT NULL, proveedor VARCHAR(50) NOT NULL, categoria ENUM('Bebidas', 'Carnes', 'Frutas', 'Verduras', 'Lacteos', 'Ropa', 'Granos', 'Otros') NOT NULL, precio_unidad SMALLINT(6) NOT NULL, cantidad_existente SMALLINT(6) NOT NULL, PRIMARY KEY (num_serie)) ENGINE = InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS usuario ( id_usuario INT NOT NULL AUTO_INCREMENT, cedula VARCHAR(10) NOT NULL, nombre VARCHAR(50) NOT NULL, apellido VARCHAR(50) NOT NULL, rol ENUM('Cajero', 'Administrador') NOT NULL, password CHAR(32) NOT NULL, PRIMARY KEY (id_usuario)) ENGINE = InnoDB DEFAULT CHARSET=utf8;